sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateNote', function(event, data){
		var id = $rootScope.noteID;
		console.log("Changing Note List " + id);
		for(var note in c.data.notes){
			if(c.data.notes[note].sys_id == id){
				c.data.notes[note].title = $rootScope.noteTitle;
				c.data.notes[note].note = $rootScope.noteBody.slice(0, 20);
			}
		}
	})
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUserID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while(noteGR.next()){
		var note = {};
		$sp.getRecordDisplayValues(note, noteGR, 'number,title,sys_id');
		note.note = noteGR.getValue('note').slice(0, 20);
		data.notes.push(note);
	}

})();
